import React, { useLayoutEffect } from 'react';
import {
    View as RNView,
    Text as RNText,
    TouchableOpacity as RNTouchableOpacity,
    Image as RNImage,
    ScrollView as RNScrollView,
} from 'react-native';

import { styled } from 'nativewind';
import { useNavigation, useRoute } from '@react-navigation/native'
import { urlFor } from '../sanity';
import {
    ArrowLeftIcon, MapPinIcon, StarIcon, ChevronRightIcon
} from 'react-native-heroicons/solid';

import {
    QuestionMarkCircleIcon
} from 'react-native-heroicons/outline';
import DishRow from '../components/DishRow';

const View = styled(RNView);
const Text = styled(RNText);
const Image = styled(RNImage);
const TouchableOpacity = styled(RNTouchableOpacity);
const ScrollView = styled(RNScrollView);


const RestaurantScreen = () => {
    const navigation = useNavigation();

    const { params: {
        id,
        imgUrl,
        title,
        rating,
        genre,
        address,
        short_description,
        dishes,
        lng,
        lat,
    } } = useRoute();

    useLayoutEffect(() => {
        navigation.setOptions({
            headerShown: false,
        });
    }, []);

    return (
        <ScrollView>
            <View className='relative'>
                <Image source={{ uri: urlFor(imgUrl).url() }}
                    className='h-56 w-full bg-gray-300 p-4' />
                <TouchableOpacity
                    onPress={() => navigation.goBack()}
                    className='absolute top-10 left-5 p-2 bg-gray-100 rounded-full'>
                    <ArrowLeftIcon
                        height={20} width={20} color="#00CCBB" />
                </TouchableOpacity>
            </View>
            <View className='bg-white'>
                <View className='px-4 pt-4'>
                    <Text className='text-3xl font-bold'>{title}</Text>
                    <View className='flex-row space-x-2 my-1'>
                        <View className='flex-row items-center space-x-1'>
                            <StarIcon size={20} color="green" opacity={0.5} />
                            <Text className='text-xs text-gray-500'>
                                <Text className='text-green-500'>{rating}</Text> - {genre}
                            </Text>
                        </View>

                        <View className='flex-row items-center space-x-1'>
                            <MapPinIcon size={20} color="gray" opacity={0.4} />
                            <Text className='text-xs text-gray-500'>
                                Nearby - {address}
                            </Text>
                        </View>
                    </View>
                    <Text className='text-gray-500 mt-2 pb-4 text-xs'>{short_description}</Text>
                </View>

                <TouchableOpacity
                    className='flex-row items-center space-x-1 p-4 border-y border-gray-300'>
                    <QuestionMarkCircleIcon color="gray" opacity={0.6} size={20} />
                    <Text className='pl-2 flex-1 text-md font-bold'>
                        Have a food allergy?
                    </Text>
                    <ChevronRightIcon color="#00CCBB" opacity={0.6} size={18} />
                </TouchableOpacity>
            </View>

            <View>
                <Text className='px-4 pt-6 mb-3 font-bold text-lg'>Menu</Text>

                {dishes.map((dish) => (
                    <DishRow
                        key={dish._id}
                        id={dish._id}
                        name={dish.name}
                        description={dish.short_description}
                        price={dish.price}
                        image={dish.image}
                    />
                ))}

            </View>
        </ScrollView>
    )
}

export default RestaurantScreen
