import {
    View as RNView,
    Text as RNText,
    TouchableOpacity as RNTouchableOpacity,
    Image as RNImage,
    ScrollView as RNScrollView,
} from 'react-native';
import React from 'react';

import { styled } from 'nativewind';
import { ArrowRightIcon } from 'react-native-heroicons/outline';
import RestaurantCard from './RestaurantCard';

const View = styled(RNView);
const Text = styled(RNText);
const Image = styled(RNImage);
const TouchableOpacity = styled(RNTouchableOpacity);
const ScrollView = styled(RNScrollView);

const FeaturedRow = ({ title, description }) => {
    return (
        <View>

            <View className='mt-4 flex-row items-center justify-between px-4'>
                <Text className='font-bold text-lg'>{title}</Text>
                <ArrowRightIcon size={20} color="#00CCBB" />
            </View>
            <Text className='text-sm text-gray-400 px-4'>{description}</Text>

            <ScrollView horizontal showsHorizontalScrollIndicator={false}
                contentContainerStyle={{
                    paddingHorizontal: 15,
                }}
                className='pt-4'>

                {/* Restaurant Cards */}
                <RestaurantCard
                    id={1}
                    imgUrl="https://links.papareact.com/gn7"
                    title="Yo! Sushi"
                    rating={4.5}
                    genre="Italian"
                    address="1000 W 4th St"
                    short_description="Pizza, Italian"
                    dishes={["Pizza", "Pasta", "Salad"]}
                    lng={-118.2437}
                    lat={34.0522}
                />

                <RestaurantCard
                    id={2}
                    imgUrl="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSu6cjCOjGy4bcnU6VSSmsg3ZIRL9WWa9QRYw&usqp=CAU"
                    title="Sashimi"
                    rating={4.7}
                    genre="japanese"
                    address="1000 W 4th St"
                    short_description="Pizza, Italian"
                    dishes={["Pizza", "Pasta", "Salad"]}
                    lng={-118.2437}
                    lat={34.0522}

                />

                <RestaurantCard
                    id={3}
                    imgUrl="https://assets.epicurious.com/photos/576450dde047a6cf1ee8268c/1:1/w_2560%2Cc_limit/miso-soup.jpg"
                    title="Miso Soup"
                    rating={4.1}
                    genre="Italian"
                    address="1000 W 4th St"
                    short_description="Pizza, Italian"
                    dishes={["Pizza", "Pasta", "Salad"]}
                    lng={-118.2437}
                    lat={34.0522}
                />

            </ScrollView>

        </View>
    )
}

export default FeaturedRow
